mapper.select:
select

from tb_blog
where is_deleted = 0

    and blog_title like concat('%', #{keyword}, '%') or blog_category_name like concat('%', #{keyword}, '%')


    and blog_status = #{blogStatus}


    and blog_category_id = #{blogCategoryId}

order by blog_id DESC

    limit #{limit} offset #{start}

select count(*)
from tb_blog
where is_deleted = 0

    and blog_title like concat('%', #{keyWords}, '%') or blog_category_name like concat('%', #{keyWords}, '%')


    and blog_status = #{blogStatus}


    and blog_category_id = #{blogCategoryId}

select
from tb_blog where blog_id in (SELECT blog_id from tb_blog_tag_relation where tag_id = #{tagId}) and is_deleted = 0 and blog_status = 1 order by blog_id DESC
limit #{limit} offset #{start}
